parameters.jsCode:
for (const item of $input.all()) {
  const data = item.json.output["Client Research Report"];

  const html = `
    <!DOCTYPE html>
    <html>
    <head>
      <title>Client Research Report</title>
      <meta charset="UTF-8">
      <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; margin: 20px; }
        h1 { color: #2c3e50; border-bottom: 2px solid #3498db; padding-bottom: 10px; }
        h2 { color: #34495e; margin-top: 25px; }
        h3 { color: #2c3e50; margin-top: 20px; }
        h4 { color: #34495e; margin-top: 15px; }
        ul { margin: 10px 0; padding-left: 20px; }
        li { margin: 5px 0; }
        p { margin: 10px 0; }
        strong { color: #2c3e50; }
        a { color: #3498db; text-decoration: none; }
        a:hover { text-decoration: underline; }
      </style>
    </head>
    <body>
      <h1>Client Research Report</h1>

      <p><strong>Client:</strong> ${data.Client}</p>
      <p><strong>Date:</strong> ${data.Date}</p>
      <p><strong>Project Objective:</strong> ${data["Project Objective"]}</p>

      <h2>Research Structure</h2>
      <ul>
        ${data["Research Structure"].map(item => `<li>${item}</li>`).join('')}
      </ul>

      <h2>Research Findings</h2>
      <h3>Client Requirements</h3>
      <ul>
        ${data["Research Findings"]["Client Requirements"].map(item => `<li>${item}</li>`).join('')}
      </ul>
      <h3>Technical Feasibility</h3>
      <ul>
        ${data["Research Findings"]["Technical Feasibility"].map(item => `<li>${item}</li>`).join('')}
      </ul>
      <h3>Workflow Optimisation</h3>
      <ul>
        ${data["Research Findings"]["Workflow Optimisation"].map(item => `<li>${item}</li>`).join('')}
      </ul>
      <h3>Pain Points</h3>
      <ul>
        ${data["Research Findings"]["Pain Points"].map(item => `<li>${item}</li>`).join('')}
      </ul>
      <h3>Pricing & Timelines</h3>
      <ul>
        ${data["Research Findings"]["Pricing & Timelines"].map(item => `<li>${item}</li>`).join('')}
      </ul>

      <h2>Client Communication</h2>
      <h3>Introduction</h3>
      <p>${data["Client Communication"].Introduction}</p>

      <h3>Workflow and Process Issues</h3>
      ${data["Client Communication"]["Workflow and Process Issues"].map(category => `
        ${Object.entries(category).map(([key, value]) => `
          <h4>${key}</h4>
          <ul>
            ${value.map(item => `<li>${item}</li>`).join('')}
          </ul>
        `).join('')}
      `).join('')}

      <h3>Summary</h3>
      <p>${Array.isArray(data["Client Communication"].Summary) 
            ? data["Client Communication"].Summary.join("<br>") 
            : data["Client Communication"].Summary}</p>

      ${data["Client Communication"].Citations 
        ? `
          <h3>Citations</h3>
          <ul>
            ${data["Client Communication"].Citations.map(link => 
              `<li><a href="${link}" target="_blank">${link}</a></li>`).join('')}
          </ul>
        `
        : ""
      }
    </body>
    </html>
  `;

  item.json.htmlEmailTemplate = html;
}

return $input.all();
